<template>    <div class="box">        <wangEditor                class="editor"                :Editorhtml="Editorhtml"                :EditorHeight='editheight'                :EditorImgUrl="EditorImgUrl"                v-on:EditorValueFn="EditorValueFn"                v-on:getWangEditorimg="getWangEditorimg"        ></wangEditor>        <div class="editor2" v-html="editorHtml"></div>    </div></template><script>    import { ref } from 'vue';    import wangEditor from '@/components/wangeditor/index.vue'    export default {        components:{wangEditor},         setup(){             let Editorhtml = ref('<h2>设置你的内容<h2>');             let editheight = ref(520);//设置高度             let editorHtml = ref('');             const EditorValueFn = str=>{//获取editor内容                 editorHtml.value = str;             }             let EditorImgUrl = ref('');             const getWangEditorimg = str=>{                 console.log(str,'上传的图片');             };             return {                 Editorhtml,                 editheight,                 EditorImgUrl,                 EditorValueFn,                 getWangEditorimg,                 editorHtml             }         }    }</script><style scoped>    .box{        width:90%;        height: 600px;        margin: 30px auto;        padding:0 10px 30px 10px;        text-align: left;    }    .editor2{        width: 48%;        border: 1px solid green;        height: 580px;        float: right;        padding: 10px;    }    .editor{        width: 48%;        float: left;    }</style>